import React, { Component } from 'react';
import CardList from '../Components/CardList';
import SearchBox from '../Components/SearchBox';
import Scroll from '../Components/Scroll';
import ErrorBoundary from '../Components/ErrorBoundary';
import NewUser from '../Components/NewUser';
import NewUsers from "../Components/NewUsers";
import './App.css';



class App extends Component {
    constructor() {
        super();
        this.state = {
            robots: [],
            searchField: '',
            userName: '',
            email: '',
            name: ''
        };
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(users => this.setState({ robots: users }));
    }

    onSearchChange = (event) => {
        this.setState({ searchField: event.target.value });
    }

    onSubmit = (event) => {
        this.setState(state => {
            this.name = document.getElementById("nameField").value;
            this.userName = document.getElementById("usernameField").value;
            this.email = document.getElementById("emailField");
        });
        console.log(this.state.name);
    }




    render() {
        const { robots, searchField } = this.state;
        const filteredRobots = robots.filter(robot => {
            return robot.name.toLowerCase().includes(searchField.toLowerCase())
        });
        return !robots.length ?
            <h1 className='tc'>Loading</h1> :
            (
                <div className='tc'>
                    <h1 className='f1'>RoboFriends</h1>
                    <SearchBox searchChange={this.onSearchChange} />
                    <form onSubmit={this.onSubmit()}>
                            <input type='text' required id='nameField' placeholder='Enter your name'/>
                            <input type='text' required id='emailField' placeholder='Enter your email'/>
                            <input type='text' required id='usernameField' placeholder='Enter your username'/>
                            <input type="submit" value="Submit"/>
                    </form>
                    <Scroll>
                        <ErrorBoundary>
                            <CardList robots={filteredRobots} />
                        </ErrorBoundary>
                    </Scroll>
                </div>
            );
    }
}

export default App;
