import React, {Component} from 'react';
import { robots } from '../Robots';

class NewUser extends Component {
    constructor(props) {
        super(props);
        this.state = {name: '', email: '', username: '', robots: this.props.robots};

        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit = (e) => {
        var newUser = {
            id : 11,
            name: this.state.name,
            email: this.state.email,
            username: this.state.username
        }
        this.setState(robots = this.state.robots.push(newUser));
        e.preventDefault();
    }

    handleNameChange = (e) => {this.setState({name: e.target.value})};

    handleUsernameChange = (e) => {this.setState({username: e.target.value})};

    handleEmailChange = (e) => {this.setState({email: e.target.value})};



    render() {
        return(
            <form onSubmit={this.handleSubmit}>
            <label>
              <input type="text" required value={this.state.name} placeholder='Enter your name' onChange={this.handleNameChange} />
              <input type="text" required value={this.state.username} placeholder='Enter your username' onChange={this.handleUsernameChange} />
              <input type="text" required value={this.state.email} placeholder='Enter your email' onChange={this.handleEmailChange} />              
            </label>
            <input type="submit" value="Submit" />
          </form>
        );
    }
}

export default NewUser;
